@Composable
fun ActivityState(
    modifier: Modifier = Modifier,
    isCompletedToday: Boolean
) {
    Row(
        modifier = modifier,
        verticalAlignment = Alignment.CenterVertically,
        horizontalArrangement = Arrangement.spacedBy(4.dp)
    ) {
        if(isCompletedToday) {
            Icon(
                imageVector = Icons.Filled.TaskAlt,
                contentDescription = "completed",
                tint = Color(0xFF518F74)
            )
            TextBody(
                color = Color(0xFF518F74),
                style = MaterialTheme.typography.bodyMedium,
                text = "Completado"
            )
        } else {
            Icon(
                imageVector = Icons.Filled.Schedule,
                contentDescription = "not completed",
                tint = Color(0xFFE1A42C)
            )
            TextBody(
                color = Color(0xFFE1A42C),
                style = MaterialTheme.typography.bodyMedium,
                text = "Pendiente"
            )
        }
    }
}

@Composable
fun ActivityCard(
    modifier: Modifier = Modifier,
    image: ImageBitmap,
    title: String,
    onClick: () -> Unit,
    isCompletedToday: Boolean
) {
    Column(
        modifier = modifier.clickable {
            if(!isCompletedToday) onClick()
        },
        verticalArrangement = Arrangement.spacedBy(4.dp)
    ) {
        Image(
            modifier = Modifier
                .size(width = 170.dp, height = 220.dp)
                .clip(RoundedCornerShape(8.dp)),
            bitmap = image,
            contentScale = ContentScale.Crop,
            contentDescription = "cover",
            colorFilter = if(isCompletedToday) ColorFilter.colorMatrix(ColorMatrix().apply { setToSaturation(0f) }) else null
        )
        TextTile(
            title = title,
            style = MaterialTheme.typography.titleMedium,
            color = MaterialTheme.colorScheme.onBackground
        )
        ActivityState(
            isCompletedToday = isCompletedToday
        )
    }
}

@Composable
fun Content(
    modifier: Modifier = Modifier,
    activities: List<ActivityCoverUiState>,
    onNavigateToIntroActivity: (Int) -> Unit,
    onNavigateToActivity: (Int) -> Unit
) {
    LazyVerticalGrid(
        modifier = modifier.padding(16.dp),
        columns = GridCells.Fixed(2),
        verticalArrangement = Arrangement.spacedBy(16.dp),
        horizontalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        items(activities) { activity ->
            ActivityCard(
                image = activity.image,
                title = activity.title,
                onClick = {
                    if(activity.authorQuote != null && activity.quote != null) {
                        onNavigateToIntroActivity(activity.id)
                    } else {
                        onNavigateToActivity(activity.id)
                    }
                },
                isCompletedToday = activity.isCompletedToday
            )
        }
    }
}

@Composable
fun ActivitiesScreen(
    modifier: Modifier = Modifier,
    activities: List<ActivityCoverUiState>,
    onNavigateToIntroActivity: (Int) -> Unit,
    onNavigateToActivity: (Int) -> Unit,
    onNavigateToResults: () -> Unit,
    onNavigateToActivities: () -> Unit,
    onNavigateToSettings: () -> Unit,
    onNavigateToTips: () -> Unit
) {
    Scaffold(
        modifier = modifier,
        content = { paddingValues ->
            Content(
                modifier = Modifier.padding(paddingValues = paddingValues),
                activities = activities,
                onNavigateToIntroActivity = onNavigateToIntroActivity,
                onNavigateToActivity = onNavigateToActivity
            )
        },
        bottomBar = {
            ZaritcareNavBar(
                onNavigateToForms = onNavigateToResults,
                onNavigateToActivities = onNavigateToActivities,
                onNavigateToTips = onNavigateToTips,
                onNavigateToSettings = onNavigateToSettings,
                selectedPage = 1
            )
        }
    )
}

